import React from "react";
import  { useState } from 'react';


export default function TextArea(props) {

  const [text, setText] = React.useState('enter ');
  const toUppercase = () =>{
    setText(text.toUpperCase);

  }
  const handleOnChange = (event) =>{
    setText(event.target.value);
  }
  return (
    <>
    <div className="container my-3">
      <h1>{props.heading}</h1>  
      <textarea className="form-control" id="myBox" value={text} onChange={handleOnChange} rows="3"></textarea>
   
    <button className="btn btn-primary" onClick={toUppercase}>toUppercase</button>
    </div>
   
    </>
  );
}
