import React from "react";
import { useState } from "react";

export default function TextArea(props) {
  const handleUppercase = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleOnChange = (event) => {
    setText(event.target.value);
  };
  const handleLowercase= ()=>{
    let newText = text.toLowerCase();
    setText(newText);
  }
  const [text, setText] = useState("enter");
  return (
    <>
      <div className="container my-3">
        <h1>{props.heading}</h1>
        <textarea
          className="form-control my-3"
          id="myBox"
          value={text}
          onChange={handleOnChange}
          rows="8"
        ></textarea>

        <button className="btn btn-primary my-4 mx-2" onClick={handleUppercase}>
          toUppercase
        </button>
        <button className="btn btn-primary my-4 mx-2" onClick={handleLowercase}>
          toLowercase
        </button>

        <div className="container my-3">
          <span>{`${text.split(" ").length} words and ${text.length} characters`} </span>
        </div>
      </div>
    </>
  );
}
